
public class GoodCalc {
	private int result= 0;
	public void add(int i, int j) {
		// TODO Auto-generated method stub
		result = i+ j ;
	}
	public	int getResult() {
		// TODO Auto-generated method stub
		return result;
	}
	public void sub(int i, int j) {
		// TODO Auto-generated method stub
		result = i-j;
	}
	public void div(int i, int j) {
		// TODO Auto-generated method stub
		result = i/j;
	}

}
